mapper.update:
UPDATE location


        class_id = #{location.classInfo.id},


        seminar_id = #{location.seminar.id},


        longitude = #{location.longitude},


        latitude = #{location.latitude},


        status = #{location.status}


WHERE
seminar_id = #{location.seminar.id}
AND
class_id = #{location.classInfo.id}

mapper.select:
SELECT COUNT(*)
FROM course
WHERE id = #{courseId}

SELECT COUNT(*)
FROM class_info
WHERE id = #{classId}

SELECT class_info.id
, class_info.name
, course_id, site, class_time, class_info.report_percentage, class_info.presentation_percentage, class_info.five_point_percentage, class_info.four_point_percentage, class_info.three_point_percentage FROM class_info, course_selection WHERE course_selection.student_id = #{userId} AND course_selection.class_id = class_info.id ;
